import { toast } from "react-hot-toast";
import { useDispatch, useSelector } from "react-redux";
import { add, remove } from "../redux/Slices/CartSlice";

const Product = ({ post }) => {
  const { cart } = useSelector((state) => state);
  const dispatch = useDispatch();

  const addToCart = () => {
    dispatch(add(post));
    toast.success("Item added to Cart");
  };

  const removeFromCart = () => {
    dispatch(remove(post.id));
    toast.error("Item removed from Cart");
  };

  return (
    <div
      className="h-[360px] w-[250px] flex flex-col items-center justify-between 
        transform hover:scale-105 transition-transform duration-300 ease-in-out 
        gap-3 p-4 mt-10 ml-5 rounded-xl shadow-lg 
        hover:shadow-xl bg-white"
    >
      {/* Title */}
      <div className="w-full">
        <p className="text-gray-800 font-semibold text-lg text-left truncate">
          {post.title}
        </p>
      </div>

      {/* Description */}
      <div className="w-full">
        <p className="text-gray-500 font-normal text-sm text-left truncate">
          {post.description.split(" ").slice(0, 10).join(" ") + "..."}
        </p>
      </div>

      {/* Image */}
      <div className="h-[140px] w-full">
        <img src={post.image} alt={post.title} className="h-full w-full object-contain" />
      </div>

      {/* Price and Action Button */}
      <div className="flex justify-between items-center w-full mt-4">
        {/* Price */}
        <p className="text-green-600 font-semibold text-lg">${post.price}</p>

        {/* Add/Remove Button */}
        {cart.some((p) => p.id === post.id) ? (
          <button
            className="text-gray-700 border border-gray-700 rounded-full font-semibold 
              text-xs px-3 py-1 uppercase 
              hover:bg-gray-700 hover:text-white transition-all duration-300 ease-in-out"
            onClick={removeFromCart}
          >
            Remove Item
          </button>
        ) : (
          <button
            className="text-gray-700 border border-gray-700 rounded-full font-semibold 
              text-xs px-3 py-1 uppercase 
              hover:bg-gray-700 hover:text-white transition-all duration-300 ease-in-out"
            onClick={addToCart}
          >
            Add to Cart
          </button>
        )}
      </div>
    </div>
  );
};

export default Product;
